mapper.select:
SELECT * FROM app_info a INNER JOIN app_oauth oa ON a.oauthId=oa.id INNER JOIN app_saml sa ON a.samlId=sa.id INNER JOIN app_sts st ON a.stsId=st.id INNER JOIN person p ON a.creator=p.id INNER JOIN department d ON a.manageDepId=d.id where p.id=#{visitorId}
and a.name LIKE CONCAT(CONCAT('%', #{searchAppByName}), '%')
order by a.id LIMIT #{index},#{len}

SELECT * FROM app_info a INNER JOIN app_oauth oa ON a.oauthId=oa.id INNER JOIN app_saml sa ON a.samlId=sa.id INNER JOIN app_sts st ON a.stsId=st.id INNER JOIN person p ON a.creator=p.id INNER JOIN department d ON a.manageDepId=d.id where manageDepId=#{currentDepId} and p.id <> #{visitorId}
and a.name LIKE CONCAT(CONCAT('%', #{searchOtherAppByName}), '%')
and p.name LIKE CONCAT(CONCAT('%', #{searchOtherAppByCreator}), '%')
order by a.id LIMIT #{index},#{len}

SELECT count(a.id) FROM app_info a INNER JOIN app_oauth oa ON a.oauthId=oa.id INNER JOIN app_saml sa ON a.samlId=sa.id INNER JOIN app_sts st ON a.stsId=st.id INNER JOIN person p ON a.creator=p.id INNER JOIN department d ON a.manageDepId=d.id where manageDepId=#{currentDepId} and p.id <> #{visitorId}
and a.name LIKE CONCAT(CONCAT('%', #{searchOtherAppByName}), '%')
and p.name LIKE CONCAT(CONCAT('%', #{searchOtherAppByCreator}), '%')
